import React, { useState, useEffect } from "react";
import StudentForm from "./StudentForm";

const COURSES = [
  "Mathematics",
  "Physics",
  "Computer Science",
  "Chemistry",
  "Biology",
  "History",
  "Economics",
  "Philosophy",
  "Engineering",
  "Art",
];

export default function StudentDashboard({ user, onLogout }) {
  const [students, setStudents] = useState([]);
  const [message, setMessage] = useState(null);
  const [editStudent, setEditStudent] = useState(null);

  // Fetch students from backend
  useEffect(() => {
    fetchStudents();
  }, []);

  async function fetchStudents() {
    try {
      const res = await fetch("http://127.0.0.1:5000/students");
      const data = await res.json();
      setStudents(data);
    } catch (err) {
      console.error(err);
      setMessage({ type: "error", text: "Unable to load students." });
    }
  }

  async function addStudent(student) {
    try {
      const res = await fetch("http://127.0.0.1:5000/students", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(student),
      });
      const body = await res.json();
      if (!res.ok) {
        setMessage({ type: "error", text: body.error || "Failed to add" });
        return;
      }
      setStudents((prev) => [...prev, body]);
      setMessage({ type: "success", text: "Student added!" });
      setTimeout(() => setMessage(null), 2000);
    } catch (err) {
      console.error(err);
      setMessage({ type: "error", text: "Network error while adding student" });
    }
  }

  async function deleteStudent(id) {
    if (!window.confirm("Delete this student?")) return;
    try {
      const res = await fetch(`http://127.0.0.1:5000/students/${id}`, {
        method: "DELETE",
      });
      if (!res.ok) {
        setMessage({ type: "error", text: "Failed to delete" });
        return;
      }
      setStudents((prev) => prev.filter((s) => s.id !== id));
      setMessage({ type: "success", text: "Student deleted" });
      setTimeout(() => setMessage(null), 1500);
    } catch (err) {
      console.error(err);
      setMessage({ type: "error", text: "Network error while deleting" });
    }
  }

  async function updateStudent(student) {
    try {
      const res = await fetch(`http://127.0.0.1:5000/students/${student.id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(student),
      });
      const data = await res.json();
      if (!res.ok) {
        setMessage({ type: "error", text: data.error || "Failed to update" });
        return;
      }
      setStudents((prev) =>
        prev.map((s) => (s.id === data.id ? data : s))
      );
      setEditStudent(null);
      setMessage({ type: "success", text: "Student updated!" });
      setTimeout(() => setMessage(null), 2000);
    } catch (err) {
      console.error(err);
      setMessage({ type: "error", text: "Network error while updating" });
    }
  }

  return (
    <div className="App">
      <h1>ðŸŽ“ Student Dashboard</h1>
      <button onClick={onLogout} style={{ position: "absolute", right: 20, top: 20 }}>
        Logout
      </button>

      {message && (
        <div className={`message ${message.type}`}>{message.text}</div>
      )}

      {/* Add new student */}
      <div className="controls">
        <StudentForm courses={COURSES} onAdd={addStudent} />
      </div>

      {/* Student cards */}
      <div className="grid">
        {students.map((s) => (
          <div className="card" key={s.id}>
            <h3>{s.name}</h3>
            <p>{s.course}</p>
            <div className="actions">
              <button className="edit" onClick={() => setEditStudent(s)}>
                Edit
              </button>
              <button className="delete" onClick={() => deleteStudent(s.id)}>
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Edit Modal */}
      {editStudent && (
        <div className="modal">
          <div className="modal-content">
            <h2>Edit Student</h2>
            <input
              type="text"
              value={editStudent.name}
              onChange={(e) =>
                setEditStudent({ ...editStudent, name: e.target.value })
              }
            />
            <select
              value={editStudent.course}
              onChange={(e) =>
                setEditStudent({ ...editStudent, course: e.target.value })
              }
            >
              {COURSES.map((c) => (
                <option key={c} value={c}>
                  {c}
                </option>
              ))}
            </select>
            <div style={{ marginTop: "15px" }}>
              <button onClick={() => updateStudent(editStudent)}>Save</button>
              <button
                onClick={() => setEditStudent(null)}
                style={{ marginLeft: "10px", background: "#d62828" }}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
