import React, { useEffect, useState } from 'react';
import StudentForm from './StudentForm';

const COURSES = [
  'Mathematics','Physics','Computer Science','Chemistry','Biology',
  'History','Economics','Philosophy','Engineering','Art'
];

export default function StudentDashboard({ user, onLogout }) {
  const [students, setStudents] = useState([]);
  const [message, setMessage] = useState(null);
  const [editing, setEditing] = useState(null);

  useEffect(() => { if(user) fetchStudents() }, [user]);

  async function fetchStudents(){
    try{
      const res = await fetch('http://127.0.0.1:5000/students');
      const data = await res.json();
      setStudents(data);
    } catch(err){ console.error(err); setMessage({type:'error', text:'Unable to load students'}); }
  }

  async function addStudent(student){
    try{
      const res = await fetch('http://127.0.0.1:5000/students', {
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body: JSON.stringify({...student, courses:[student.course]})
      });
      const body = await res.json();
      if(!res.ok){ setMessage({type:'error', text: body.error || 'Failed to add'}); return; }
      setStudents(prev => [...prev, body]);
      setMessage({type:'success', text:'Student added!'});
      setTimeout(()=>setMessage(null),2000);
    }catch(err){ console.error(err); setMessage({type:'error', text:'Network error while adding student'}); }
  }

  async function updateStudent(id, student){
    try{
      await fetch(`http://127.0.0.1:5000/students/${id}`, {
        method:'PUT',
        headers:{'Content-Type':'application/json'},
        body: JSON.stringify({ name: student.name, courses:[student.course] })
      });
      fetchStudents();
      setEditing(null);
      setMessage({type:'success', text:'Student updated!'});
      setTimeout(()=>setMessage(null),1500);
    } catch(err){ console.error(err); }
  }

  async function deleteStudent(id){
    if(!window.confirm('Delete this student?')) return;
    try{
      const res = await fetch(`http://127.0.0.1:5000/students/${id}`, { method:'DELETE'});
      if(!res.ok){ setMessage({type:'error', text:'Failed to delete'}); return; }
      setStudents(prev => prev.filter(s=>s.id!==id));
      setMessage({type:'success', text:'Student deleted'});
      setTimeout(()=>setMessage(null),1500);
    }catch(err){ console.error(err); setMessage({type:'error', text:'Network error while deleting'}); }
  }

  return (
    <div>
      <h1>ðŸŽ“ Student Dashboard</h1>
      <button onClick={onLogout} style={{position:'absolute', right:20, top:20}}>Logout</button>
      {message && <div className={`message ${message.type==='success'?'success':'error'}`}>{message.text}</div>}

      {!editing && <StudentForm courses={COURSES} onAdd={addStudent} />}

      <div className="grid">
        {students.map(s => (
          <div className="card" key={s.id}>
            {editing && editing.id===s.id ? (
              <StudentForm 
                courses={COURSES} 
                initialValues={{name:s.name, course:s.courses[0]?.name}}
                onSubmit={(values)=>updateStudent(s.id, values)}
                submitLabel="Update"
              />
            ) : (
              <>
                <h3>{s.name}</h3>
                <p>{s.courses.map(c=>c.name).join(', ')}</p>
                <div className="actions">
                  <button onClick={()=>setEditing(s)}>Edit</button>
                  <button onClick={()=>deleteStudent(s.id)}>Delete</button>
                </div>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
